class Api {
  constructor(options) {
    this._url = options.url;
  }

  _checkResponse(res) {
    if (res.ok) {
      return Promise.resolve(res.json())
    }
    return Promise.reject(`Ошибка ${res.status}`);
  }

  _request(path, method, data) {
    let body = data;
    if((method === "PATCH" || method === "POST") && data) {
      body = JSON.stringify(data);
    }
    return fetch(this._url + path, {
      method,
      headers: {
      Authorization: `Bearer ${localStorage.getItem("jwt")}`},
      body,
    })
    .then(this._checkResponse);
  }

  async getUserInfo() {
    const response = await fetch(`${this._url}/users/me`, {
      headers: {
        "Content-Type": "application/json",
        authorization: `Bearer ${localStorage.getItem("jwt")}`,
      },
    })
    return this._checkResponse(response)
  }

  async setUserInfo(data) {
    const response = await fetch(`${this._url}/users/me`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        authorization: `Bearer ${localStorage.getItem("jwt")}`,
      },
      body: JSON.stringify({
        name: data.name,
        about: data.about,
      }),
    })
    return this._checkResponse(response)
  }


  getItems() {
    return this._request(`cards`, "GET");
  }

  async getItems() {
    const response = await fetch(`${this._url}/cards`, {
      headers: {
        "Content-Type": "application/json",
        authorization: `Bearer ${localStorage.getItem("jwt")}`,
      },
    })
    return this._checkResponse(response)
  }

  setAvatar(data) {
    return this._request(`users/me/avatar`, "PATCH", data);
  }

  addCard(data) {
    return this._request(`cards`, "POST", data);
  }

  deleteCard(id) {
    return this._request(`cards/${id}`, "DELETE");
  }

  changeLikeCardStatus(cardId, isLiked) {
    if (isLiked) {
      return this._request(`cards/${cardId}/likes`, "PUT");
    } else {
      return this._request(`cards/${cardId}/likes`, "DELETE");
    }
  }
}

export const api = new Api({
  url: "http://localhost:3000",
});
